mapper.select:
SELECT article_no, parent_no, title, content, image_file_name, write_date, id
  FROM t30_board
 WHERE article_no = #{articleNo}

mapper.update:
UPDATE t30_board
   SET title = #{title}
     , content = #{content}

     , image_file_name = #{imageFileName}

 WHERE article_no = #{articleNo}
